import axios from "axios";

const basUrl = 'https://social-network.samuraijs.com/api/1.0'

export const getUsers = (currentPage, pageSize) => {
    return axios.get(basUrl + `/users?page=${currentPage}&count=${pageSize}`, {
        withCredentials: true
    }).then(response => response.data)
}
