import React from 'react';
import c from './ProfileInfo.module.css';

class ProfileStatus extends React.Component ()  {

    state = {
        editMode: true
    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                    <div>
                        <span>{this.props.status}</span>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input value={this.props.status}></input>
                    </div>
                }
            </div>
        )
    }
}

export default ProfileStatus;
