import React from 'react';
import { reduxForm } from 'redux-form';
import { createField, Input, Textarea } from '../../common/FormsControllers/FormsControllers';
import c from './ProfileInfo.module.css';

const ProfileDataForm = ({ handleSubmit, profile }) => {
    return <form onSubmit={handleSubmit}>
        <div>
            <button>Save</button>
        </div>
        <div>
            <b>Full name:</b> { createField("Full name", "fullName", [], Input) }
        </div>
        <div>
            <b>Looking for a job:</b> { createField("", "lookingForAJob", [], Input, {type: "checkbox"}) }
        </div>
            <div>
                <b>My professional skills:</b> { createField("My professional skills", "lookingForAJobDescription", [], Textarea) }
            </div>
        <div>
            <b>About me:</b> { createField("About me", "aboutMe", [], Textarea) }
        </div>
        <div>
            <b>Contacts:</b> {Object.keys(profile.contacts).map(key => {
                return <div className={c.contact} >
                    <b>{key}:</b> { createField(key, "contacts." + key, [], Input) }
                </div>
            })}
        </div>
    </form>
}

const ProfileDataFormReduxForm = reduxForm({ form: "edit-profile", enableReinitialize: true, destroyOnUnmount: false })(ProfileDataForm);


export default ProfileDataFormReduxForm;
