let rerenderEntireTree = (state) => {
  root.render(
    <React.StrictMode>
      <App store={store} />
    </React.StrictMode>
  );
}

reportWebVitals();


rerenderEntireTree(store.getState());

store.subscribe (() => {
  let state = store.getState();
  rerenderEntireTree(state);
});
