const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';

let initialState = {
    users: [
        /* { id: 1, isFollowed: true, fullName: 'Tatiana', status: 'Strong', location: {city: 'Saint-Petersburg', country: 'Russia'} },
        { id: 2, isFollowed: false, fullName: 'Natalie', status: 'Independent', location: {city: 'Saint-Petersburg', country: 'Russia'} },
        { id: 3, isFollowed: false, fullName: 'Dora', status: 'Powerful', location: {city: 'Saint-Petersburg', country: 'Russia'} },
        { id: 4, isFollowed: true, fullName: 'Ola', status: 'Beautiful', location: {city: 'Saint-Petersburg', country: 'Russia'} }, */
    ],
    newUserText: 'newPost',
}

const usersReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, isFollowed: true}
                    }
                    return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, isFollowed: false}
                    }
                    return u
                })
            } 
            case SET_USERS:
                return {
                    ...state,
                    users: [...state.users, ...action.users]
                }
        default:
            return state;
    }
}

export const followAC = (userId) => ({type: FOLLOW, userId})

export const unfollowAC = (userId) => ({type: UNFOLLOW, userId})

export const setUsersAC = (users) => ({type: SET_USERS, users})

export default usersReducer;
